import React, { useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";

const Chat = ({ location }) => {
  useEffect(() => {
    const data = queryString.parse(location.search);

    console.log(location.search, data);
  });
};

export default Chat;
